import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import styles from "./Frame111.module.css";
import * as XLSX from "xlsx";

const Frame111 = () => {
  const navigate = useNavigate();
  const [wordData, setWordData] = useState([]);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("/Combined_MZ_Words.xlsx");

        const arrayBuffer = await response.arrayBuffer();
        const data = new Uint8Array(arrayBuffer);
        const workbook = XLSX.read(data, { type: "array" });

        const sheetName = workbook.SheetNames[0];
        const sheet = workbook.Sheets[sheetName];

        const jsonData = XLSX.utils.sheet_to_json(sheet, { header: 1 });

        const columnNames = jsonData[0];
        jsonData.shift();

        const transformedData = jsonData.map((row) => {
          const wordObject = {};
          columnNames.forEach((column, index) => {
            wordObject[column] = row[index];
          });
          return wordObject;
        });

        setWordData(transformedData);
      } catch (error) {
        console.error("Error loading data:", error);
      }
    };

    fetchData();
  }, []);

  // 2초에 한 번씩 currentWordIndex를 증가시켜 단어를 변경
  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentWordIndex((prevIndex) =>
        prevIndex < wordData.length - 1 ? prevIndex + 1 : 0
      );
    }, 2000);

    return () => clearInterval(intervalId); // 컴포넌트가 언마운트될 때 clearInterval
  }, [wordData]);

  const onRegisButtonContainerClick = () => {
    navigate("/8");
  };

  const onMZTextClick = () => {
    navigate("/");
  };

  const onMZText1Click = () => {
    navigate("/");
  };

  const onMZText3Click = () => {
    navigate("/2");
  };

  return (
    <div className={styles.div}>
      <div className={styles.inner}>
        <div className={styles.container}>
          <div className={styles.div4}>
            {wordData.length > 0 && (
              <>
                <p className={styles.p}>{wordData[currentWordIndex]["한글 단어"]}</p>
                <p className={styles.p}>{wordData[currentWordIndex]["영어 단어"]}</p>
                <p className={styles.p}>{wordData[currentWordIndex]["한글 뜻"]}</p>
                <p className={styles.p}>{wordData[currentWordIndex]["영어 뜻"]}</p>
              </>
            )}
          </div>
        </div>
      </div>
      {/* ... (existing JSX) */}
    </div>
  );
};

export default Frame111;
