import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import styles from "./Frame111.module.css";
import * as XLSX from "xlsx";

const Frame111 = () => {
  const navigate = useNavigate();
  const [wordData, setWordData] = useState([]);
  const [currentRow, setCurrentRow] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("Combined_MZ_Words.xlsx");
        const arrayBuffer = await response.arrayBuffer();
        const data = new Uint8Array(arrayBuffer);
        const workbook = XLSX.read(data, { type: "array" });

        const sheetName = workbook.SheetNames[0];
        const sheet = workbook.Sheets[sheetName];

        const jsonData = XLSX.utils.sheet_to_json(sheet, { header: 1 });

        // Assuming the first row contains column headers
        const columnNames = jsonData[0];

        jsonData.shift(); // Remove the first row (column headers)

        const transformedData = jsonData.map((row) => {
          const wordObject = {};
          columnNames.forEach((column, index) => {
            wordObject[column] = row[index];
          });
          return wordObject;
        });

        setWordData(transformedData);
      } catch (error) {
        console.error("Error loading data:", error);
      }
    };

    fetchData();
  }, []);

  const handleNextRowClick = useCallback(() => {
    if (currentRow < wordData.length - 1) {
      setCurrentRow((prevRow) => prevRow + 1);
    } else {
      // You can handle reaching the end of the rows here.
      console.log("End of rows reached");
    }
  }, [currentRow, wordData]);

  // Rest of your code...

  return (
    <div className={styles.div}>
      {/* ... (Your existing JSX) */}

      <button onClick={handleNextRowClick}>Next Row</button>
    </div>
  );
};

export default Frame111;
