import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import styles from "./Frame111.module.css";
import * as XLSX from "xlsx";

const ITEMS_PER_PAGE = 10;

const Frame111 = () => {
  const navigate = useNavigate();
  const [wordData, setWordData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("/Modified_MZ_Words.xlsx");

        const arrayBuffer = await response.arrayBuffer();
        const data = new Uint8Array(arrayBuffer);
        const workbook = XLSX.read(data, { type: "array" });

        const sheetName = workbook.SheetNames[0];
        const sheet = workbook.Sheets[sheetName];

        const jsonData = XLSX.utils.sheet_to_json(sheet, { header: 1 });

        const columnNames = jsonData[0];
        jsonData.shift();

        const transformedData = jsonData.map((row) => {
          const wordObject = {};
          columnNames.forEach((column, index) => {
            wordObject[column] = row[index];
          });
          return wordObject;
        });

        setWordData(transformedData);
      } catch (error) {
        console.error("Error loading data:", error);
      }
    };

    fetchData();
  }, []);

  const onPageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  const onRegisButtonContainerClick = () => {
    navigate("/8");
  };

  const onMZTextClick = () => {
    navigate("/");
  };

  const onMZText1Click = () => {
    navigate("/");
  };

  const onMZText3Click = () => {
    navigate("/2");
  };

  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;

  return (
    <div className={styles.div}>
      <div className={styles.inner}>
        <div className={styles.container}>
          <div className={styles.div4}>
            {/* Display the current set of 10 items */}
            {wordData.slice(startIndex, endIndex).map((word, index) => (
              <React.Fragment key={index}>
                <p className={styles.p}>{word["한글 단어"]}</p>
                <p className={styles.p}>{word["영어 단어"]}</p>
                <p className={styles.p}>{word["한글 뜻"]}</p>
                <p className={styles.p}>{word["영어 뜻"]}</p>
              </React.Fragment>
            ))}
          </div>
        </div>
      </div>
      {/* ... (existing JSX) */}
      <div>
        {/* Pagination controls */}
        <button
          disabled={currentPage === 1}
          onClick={() => onPageChange(currentPage - 1)}
        >
          Previous
        </button>
        <span>{`Page ${currentPage}`}</span>
        <button
          disabled={endIndex >= wordData.length}
          onClick={() => onPageChange(currentPage + 1)}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default Frame111;
